import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import Header from "./Header";
import Note from "./Note";
import Footer from "./Footer";

function App() {

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [backendData, setBackendData] = useState([{}]); // storing backend data
  const [dataUpdated, setDataUpdated] = useState(true); // useeffect dependency

  function handleChangeTitle(event) { 
  //   console.log(event.target.value);
    setTitle(event.target.value);
  }

  function handleChangeContent(event) {
    // console.log(event.target.value);
    setContent(event.target.value);
  }

  function deleteNote(id) {
    console.log(`trying to delete ${id}`)
    // const updatedNotes = allNotes.filter((note) => note.key !== id);

    // const updatedNotesKeys = updatedNotes.map((note) => {
    //   if (note.key > id) {
    //     return { ...note, key: note.key - 1 };
    //   }
    //   return note;
    // });

    // setAllNotes(updatedNotesKeys);

    async function deleteData () {
      console.log("deleting data");
      const response = await fetch("http://localhost:5000/api/deleteData", {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },

        body: JSON.stringify({
          id: id.toString()
        })
      })
      if(response?.status === 200) {
        setDataUpdated(!dataUpdated);
      }
    };

    deleteData();
  }

  function CreateNotes(note) {
    return (
      <Note
        key={note._id}
        id={note._id}
        title={note.title}
        content={note.content}
        onDelete={deleteNote}
      />
    );
  }


  function handleClick(event) {
    console.log(title);
    console.log(content);

    event.preventDefault();

    if (title.trim() === "" && content.trim() === "") {
      alert("Please enter a title and content for your note");
      return;
    } else if (title.trim() === ""){
      alert ("Please enter a title for your note");
      return;
    } else if (content.trim() === "") {
      alert("Please enter some content in your note");
      return;
    }


    //write to mongodb
    async function postData () {
      console.log("posting data");
      const response = await fetch("http://localhost:5000/api/postData", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },

        // converting userinput into json 
        body: JSON.stringify({
          title: title,
          content: content,
        })
      })
      if(response?.status === 200) {
        setDataUpdated(!dataUpdated);
        setTitle("");
        setContent("");
      }
    };

    postData(); // sending data 

    //fetch from mongodb


    //setAllNotes([...allNotes, newNote]);


    // setTitle("");
    // setContent("");
    // event.preventDefault();
  }

  useEffect(()=>{
    fetch("/api").then(
      response => response.json() 
    ).then(
      data =>{
        setBackendData(data);
        console.log(data);
      }
    )
  },[dataUpdated])

  return (
    <div className="container">
      <Header />
      <form onSubmit={handleClick}>
        <input
          onChange={handleChangeTitle}
          type="text"
          placeholder="Title"
          value={title}
        />
        <br />
        <textarea
          onChange={handleChangeContent}
          type="text"
          placeholder="Take a note..."
          value={content}
          rows="2"
        />
        <br />
        <button type="submit" onClick={handleClick}>
          +
        </button>
      </form>
        {backendData.map(CreateNotes)}
      <Footer />
    </div>
  );
}

export default App;
